	@Test
	public void testTimeout () throws EncodingFailedException, L2pSecurityException, SerializationException, NoSuchAlgorithmException, CryptoException, MalformedXMLException, InterruptedException {
		UserAgent a = UserAgent.createUserAgent("passa");
		UserAgent b = UserAgent.createUserAgent("passb");
		
		a.unlockPrivateKey("passa");
		b.unlockPrivateKey("passb");
		
		
		Date d = new Date();
		Message m = new Message ( a, b, "ein test");
		Date d2 = new Date();
		
		assertTrue ( d.getTime() <= m.getTimestamp());
		assertTrue ( m.getTimestamp () <= d2.getTime());
	
		assertEquals ( Message.DEFAULT_TIMEOUT, m.getTimeoutDate().getTime() - m.getTimestampDate().getTime());
		
		String xml = m.toXmlString();		
		
		System.out.println ( xml );
		
		assertTrue ( xml.contains ( "generated=\"" ));
		assertTrue ( xml.contains ( "timeout=\"" ));
		
		Message fromXml = Message.createFromXml(xml);
		
		assertEquals ( m.getTimestamp(), fromXml.getTimestamp());
		assertEquals ( m.getTimeoutDate(), fromXml.getTimeoutDate());
		
		
		m = new Message ( a,b, "ein test", 500);
		assertFalse ( m.isExpired() );
		assertEquals ( 500, m.getTimeoutDate().getTime() - m.getTimestamp() );
		
		Thread.sleep(1000);
		assertTrue ( m.isExpired ());		
	}
	
	@Test
	public void testResponseConstructor () throws EncodingFailedException, L2pSecurityException, SerializationException, NoSuchAlgorithmException, CryptoException, MalformedXMLException, AgentNotKnownException {
		UserAgent a = UserAgent.createUserAgent("passa");
		UserAgent b = UserAgent.createUserAgent("passb");
		
		a.unlockPrivateKey("passa");
		b.unlockPrivateKey("passb");
		BasicAgentStorage storage = new BasicAgentStorage();
		storage.registerAgents(a, b);
		
		Message m = new Message ( a, b, "some content");
		m.open(b,storage);
		
		Message testee = new Message ( m, "some answer");
		
		assertEquals ( a.getId(), m.getSender().getId());		
		assertEquals ( b.getId(), m.getRecipient().getId());
		
		assertEquals ( m.getRecipientId(), testee.getSenderId());
		assertEquals ( m.getSenderId(), testee.getRecipientId());
		
		assertNotNull ( testee.getResponseToId());
		assertEquals ( m.getId(), testee.getResponseToId().longValue());
		
		testee.open(a, storage);
		assertEquals ( a.getId(), testee.getRecipient().getId());
		assertEquals ( b.getId(), testee.getSender().getId());		
		
		
		String xml = testee.toXmlString();
				
		assertTrue ( xml.contains ( "responseTo=\"" + m.getId() + "\""));
		
		Message andBack = Message.createFromXml(xml);
		
		assertEquals ( a.getId(), andBack.getRecipientId());
		assertEquals ( b.getId(), andBack.getSenderId());
		assertEquals ( m.getId(), andBack.getResponseToId().longValue());
		
		andBack.open(a, storage);
	}
	
	
	@Test
	public void testFromString() throws L2pSecurityException, NoSuchAlgorithmException, CryptoException, EncodingFailedException, SerializationException, MalformedXMLException, AgentNotKnownException {
		UserAgent a = UserAgent.createUserAgent("passa");
		UserAgent b = UserAgent.createUserAgent("passb");
		
		BasicAgentStorage storage = new BasicAgentStorage ();
		storage.registerAgents ( a, b );
		
		a.unlockPrivateKey("passa");
		b.unlockPrivateKey("passb");
		
		String content = "some content";
		Message m = new Message ( a, b, content);
		
		try {
			m.getContent();
			fail ( "L2pSecurityException expected");
		} catch ( L2pSecurityException e) {
			// intended 
		}
		m.open(b, storage);
		assertEquals ( content, m.getContent() );


		String xml = m.toXmlString();
		
		Message andBack = Message.createFromXml(xml);
		
		assertEquals ( m.getSender().getId(), andBack.getSenderId());
		assertEquals ( m.getRecipient().getId(), andBack.getRecipientId());
				
		andBack.open(b, storage);
		assertEquals ( m.getContent(), andBack.getContent());
		assertEquals ( content, andBack.getContent() );
	}
		
	@Test
	public void testOpen () throws CryptoException, L2pSecurityException, EncodingFailedException, SerializationException, AgentNotKnownException {
		
		UserAgent a = UserAgent.createUserAgent("passa");
		UserAgent b = UserAgent.createUserAgent("passb");
		
		BasicAgentStorage storage = new BasicAgentStorage ();
		storage.registerAgents ( a, b );
		
		a.unlockPrivateKey("passa");
		
		Message testee = new Message( a, b, "some content");		
		assertNull ( testee.getSender());
		assertNull (testee.getRecipient());
		assertEquals ( b.getId(), testee.getRecipientId());
		assertEquals ( a.getId(), testee.getSenderId());
		
		
		b.unlockPrivateKey("passb");
		testee.open( b, storage );
		
		assertNotSame ( a, testee.getSender() );
		assertEquals ( b, testee.getRecipient ());
		assertEquals ( a.getId(), testee.getSender().getId());
	}
	
	@Test
	public void testRMIMessage () throws MalformedXMLException, IOException, CryptoException, L2pSecurityException, EncodingFailedException, SerializationException, AgentNotKnownException {
		BasicAgentStorage storage = new BasicAgentStorage ();
		UserAgent eve = MockAgentFactory.getEve();
		ServiceAgent service = ServiceAgent.generateNewAgent("i5.las2peer.api.TestService", "a pass");
		storage.registerAgents(eve, service);
		
		eve.unlockPrivateKey("evespass");
		Message m = new Message( eve, service, new RMITask ( "i5.las2peer.api.TestService", "inc", new Serializable[] { new Integer(10) }));

		String xml = m.toXmlString();
		
		Message back = Message.createFromXml(xml);
		
		service.unlockPrivateKey("a pass");
		back.open( service, storage );
		
		RMITask content = (RMITask ) back.getContent();
		
		assertEquals ( new Integer(10), content.getParameters()[0]);	
	}
		
	
